import React from 'react'
import createReactClass from 'create-react-class';
//import $ from 'jquery';


const Helper = {
  GetData: () => {
    fetch('http://localhost:8888/graphql', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        query: `
              {users{
                  name
                  users{
                      name {
                      title
                      first
                      last
                      }
                      gender
                      email
                  }
              }}
          `,
        variables: {
          now: new Date().toISOString(),
        },
      }),
    })
      .then((res) => res.json())
      .then((result) => {
          console.log(result);
          this.Data=result.data; 
          return true;
          //this.setState({data : result.data}) 
      });
      return false;
  },
  Data : {}
}



const Refresh = createReactClass({
  render: () => {
      return (
          <div>
              <input type="submit" value="Search"  />
              
          </div>
      );
  }
});



function Users() {
  return (
    <div>
          <header id="users">
              <div className="container">
                   <Refresh/>
              </div>
          </header>
      </div>
  );
}

export default Users;

