import React, { Component } from 'react'
import './User.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import DropdownButton from './react-bootstrap/DropdownButton';
import Dropdown from './react-bootstrap/Dropdown'

export class Users extends Component {
  state = {
    data: {}
  }

  getSunbau() {
    fetch('http://localhost:8888/graphql', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: `
                {users{
                    name
                    users{
                        name {
                        title
                        first
                        last
                        }
                        gender
                        email
                    }
                }}
            `,
          variables: {
            now: new Date().toISOString(),
          },
        }),
      })
        .then((res) => res.json())
        .then((result) => {
            console.log(result);
            this.setState({data : result.data}) 
        });

  }

  componentDidMount() {
    this.getSunbau();
  }

  render() {
    return (
      <div>
          <header id="users">
            <div className="container">
                <DropdownButton
                  alignRight
                  title="Dropdown right"
                  id="dropdown-menu-align-right">
                  <Dropdown.Item eventKey="option-1">option-1</Dropdown.Item>
                  <Dropdown.Item eventKey="option-2">option-2</Dropdown.Item>
                  <Dropdown.Item eventKey="option-3">option 3</Dropdown.Item>
                  <Dropdown.Divider />
                  <Dropdown.Item eventKey="some link">some link</Dropdown.Item>
                </DropdownButton>
                {this.state.data.users
                    ? this.state.data.users.map((d, i) => (
                        <div className="countries row d-none" id={d.name}>
                            <div class="container" className="col-md-12 col-sm-12 thema" key={`divhome-${i}`}>
                                <h4>{d.name}</h4>
                                <div class="row">
                                    <div class="col-1">Title</div>
                                    <div class="col-3">First Name</div>
                                    <div class="col-3">Last Name</div>
                                    <div class="col-1">Gender</div>
                                    <div class="col-4">Email</div>
                                </div>
                            
                                {d.users.map((user,j) => {
                                    return (
                                        <div class="row">
                                            <div class="col-1">{user.name.title}</div>
                                            <div class="col-3">{user.name.first}</div>
                                            <div class="col-3">{user.name.last}</div>
                                            <div class="col-1">{user.gender}</div>
                                            <div class="col-4">{user.email}</div>
                                        </div>
                                    )
                                })}
                            </div>
                        </div>
                    ))
                    : "loading"}
                    
                </div>
          </header>
      </div>
    );
  }
}

export default Users;
