import React, { Component } from 'react'
import ReactDOM from 'react-dom/client';
import './User.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown'
//import $ from 'jquery';


var Helper = {
  GetData: () => {
    alert("geldim");
    fetch('http://localhost:8888/graphql', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        query: `
              {users{
                  name
                  users{
                      name {
                      title
                      first
                      last
                      }
                      gender
                      email
                  }
              }}
          `,
        variables: {
          now: new Date().toISOString(),
        },
      }),
    })
      .then((res) => res.json())
      .then((result) => {
          alert("deneme");
          console.log(result);
          this.Data=result.data; 
          return true;
          //this.setState({data : result.data}) 
      });
      return false;
  },
  Data : {}
}

var Country = React.createClass({
  mixins: [Helper],
  getInitialState: ()=> {
      return { 
        data: {} 
      };
  },
  componentDidMount: ()=>{
    if (
      this.Data !== null && 
      this.Data !== undefined 
    )
    {
      var usersOfCOuntry = this.Data.users.filter(x => function (x){
        if(x.name === this.props.name)
        {
          return x.users;
        }
      })
      console.log(usersOfCOuntry)
      this.setState({data : usersOfCOuntry});
    } 
  },
  render: function() {
      return (
          <div>
            <div className="container">
              <h4>{this.props.name}</h4>
              <div className="row">
                  <div className="col-2">Title</div>
                  <div className="col-2">First Name</div>
                  <div className="col-3">Last Name</div>
                  <div className="col-1">Gender</div>
                  <div className="col-4">Email</div>
              </div>
              {this.state.data.users.map((user,j) => {
                  return (
                      <div className="row" key={'divUser'+j}>
                          <div className="col-2">{user.name.title}</div>
                          <div className="col-2">{user.name.first}</div>
                          <div className="col-3">{user.name.last}</div>
                          <div className="col-1">{user.gender}</div>
                          <div className="col-4">{user.email}</div>
                      </div>
                  )
              })}
          </div>
        </div>
      );
  }
});

var Countries = React.createClass({
  mixins: [Helper],
  getInitialState: ()=> {
      return { 
        showResults: false,
        data: {},
        country: "", 
      };
  },
  onSelect: (e)=> {
    this.setState({ country : e });
    this.setState({ showResults: 
      this.Data !== null && 
      this.Data !== undefined ? true : false
    });
  },
  componentDidMount: ()=>{
    if (
      this.Data !== null && 
      this.Data !== undefined 
    )
    {
      this.setState({data : this.Data});
    } 
  },
  render: function() {
      return (
          
          <div>
              <DropdownButton
                  title="Please select a Country"
                  id="countries"
                  onSelect={this.onSelect}
                  >
                    {this.state.data.users
                    ? this.state.data.users.map((d, i) => (
                        <Dropdown.Item eventKey={'divCountry'+i}>{d.name}</Dropdown.Item>
                    ))
                    : "Countries not loaded"}
                </DropdownButton>
              { this.state.showResults ? <Country name={this.state.country}/> : null }
          </div>
      );
  }
});

