import React from 'react'
import ReactDOM from 'react-dom/client';
import createReactClass from 'create-react-class';
//import $ from 'jquery';

let Helper = {
  Data: null,
  GetData: () => {
    var returnValue = false;
    fetch('http://localhost:8888/graphql', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        query: `
              {users{
                  name
                  users{
                      name {
                      title
                      first
                      last
                      }
                      gender
                      email
                  }
              }}
          `,
        variables: {
          now: new Date().toISOString(),
        },
      }),
    })
    .then((res) => res.json())
    .then((result) => {
        console.log(result.Data)
        //this.Data = result.data
        //returnValue = true; 
    });
    //return returnValue;
  }
}



const Refresh2 = createReactClass({
  getInitialState: () => {
       return { showResults: false };
  },
  onClick: () => {
    var data = this.GetData();
    console.log("deneme");
    this.setState({ showResults: data!=null?true:false });
  },
  render: () => {
      return (
          <div>
            <button
              type="submit" 
              value="Refresh"
              onClick={this.refreshData}
            >
              Refresh
            </button>
              
          </div>
      );
  }
});


function Refresh() {
  var showResult = false;
  function refreshData(e) {
    var showResult = Helper.GetData();
    alert(showResult);
  }
  return (
    <div>
      <button
        type="submit" 
        value="Refresh"
        onClick={refreshData}
      >
        Refresh
      </button>
      { showResult ? <div>deneme</div> : null }
    </div>
);
}


function Users() {
  return (
    <div>
          <header id="users">
              <div className="container">
                   <Refresh/>
              </div>
          </header>
      </div>
  );
}

export default Users;

