import React, { Component } from 'react'
import './User.css';
import 'bootstrap/dist/css/bootstrap.css';

export class Users extends Component {
  state = {
    data: {}
  }

  getSunbau() {
    fetch('http://localhost:8888/graphql', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: `
                {users{
                    name
                    users{
                        name {
                        title
                        first
                        last
                        }
                        gender
                        email
                    }
                }}
            `,
          variables: {
            now: new Date().toISOString(),
          },
        }),
      })
        .then((res) => res.json())
        .then((result) => {
            console.log(result);
            this.setState({data : result.data}) 
        });

  }

  componentDidMount() {
    this.getSunbau();
  }

  render() {
    return (
      <div>
          <header id="users">
            <div className="container">
                {this.state.data.users
                    ? this.state.data.users.map((d, i) => (
                        <div className="row">
                            
                                
                            <div class="container" className="col-md-12 col-sm-12 thema" key={`divhome-${i}`}>
                                <h4>{d.name}</h4>
                                <div class="row">
                                    <div class="col-1">Title</div>
                                    <div class="col-2">First Name</div>
                                    <div class="col-3">Last Name</div>
                                    <div class="col-1">Gender</div>
                                    <div class="col-3">Email</div>
                                </div>
                            
                                {d.users.map((user,j) => {
                                    return (
                                        <div class="list simple-list">
                                            <ul>
                                                <li className="title">{user.name.title}</li>
                                                <li className="first">{user.name.first}</li>
                                                <li className="last">{user.name.last}</li>
                                                <li className="gender">{user.gender}</li>
                                                <li className="email">{user.email}</li>
                                            </ul>
                                        </div>
                                    )
                                })}
                            </div>
                            
                        </div>
                    ))
                    : "loading"}
                    
                </div>
          </header>
      </div>
    );
  }
}

export default Users;
