import React from 'react'
import ReactDOM from 'react-dom/client';
import createReactClass from 'create-react-class';
//import $ from 'jquery';


const Helper = {
  GetData: async () => {
    
    const response = await fetch('http://localhost:8888/graphql', 
                      {
                        method: 'POST',
                        headers: {
                          'Content-Type': 'application/json',
                        },
                        body: JSON.stringify({
                          query: `
                                {users{
                                    name
                                    users{
                                        name {
                                        title
                                        first
                                        last
                                        }
                                        gender
                                        email
                                    }
                                }}
                            `,
                          variables: {
                            now: new Date().toISOString(),
                          },
                        }),
                      })
      // .then((res) => res.json())
      // .then((result) => {
        
      //     console.log(result.data);
      //     returnValue = result.data;
        
      //     //this.setState({Data : result.data}) 
      // });
      const data = await response.json(); 
      
      return data.Promise().value;
  }
}



const Refresh2 = createReactClass({
  getInitialState: () => {
       return { showResults: false };
  },
  onClick: () => {
    var data = this.GetData();
    console.log(data);
    this.setState({ showResults: data!=null?true:false });
  },
  render: () => {
      return (
          <div>
            <button
              type="submit" 
              value="Refresh"
              onClick={this.refreshData}
            >
              Refresh
            </button>
              
          </div>
      );
  }
});


function Refresh() {
  var showResult = false;
  function refreshData(e) {
    var data = Helper.GetData();
    console.log(data);
  }
  return (
    <div>
      <button
        type="submit" 
        value="Refresh"
        onClick={refreshData}
      >
        Refresh
      </button>
      { showResult ? <div>deneme</div> : null }
    </div>
);
}


function Users() {
  return (
    <div>
          <header id="users">
              <div className="container">
                   <Refresh/>
              </div>
          </header>
      </div>
  );
}

export default Users;

