import {React,Component} from 'react'
import './User.css';
import 'bootstrap/dist/css/bootstrap.min.css';
//import $ from 'jquery';


function Countries() {
    return (
        <div>
            {this.props.Data.Users}
            Ãœlkeler
        </div>
    );
}



export class Users extends Component {
  state = {
    refreshData: false,
    data: {},
    showList: false,    
  }

  componentDidMount() {
    //console.log("geldi")
    this.getData();
    
  }

  // componentWillUnmount() {
  //   alert("gelecek")
  // }

  componentDidUpdate() {
    if(this.state.refreshData)
    { 
      //console.log("test")
      this.getData();
    }
    //console.log(this.state.data)
  }
  
  getData() {
    fetch('http://localhost:8888/graphql', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: `
                {users{
                    name
                    users{
                        name {
                        title
                        first
                        last
                        }
                        gender
                        email
                    }
                }}
            `,
          variables: {
            now: new Date().toISOString(),
          },
        }),
      })
        .then((res) => res.json())
        .then((result) => {
            //console.log(result);
            this.setState({
              data : result.data,
              refreshData:false,
              showList:true
            }) 
      });
  }

  render() {

    const refreshData=(e) => {
      this.setState({refreshData: true})
    }

    return (
      <div>
        <header id="users">
          <div className="container">
            <div className="row">
              <div className="col-10">
                { this.state.showList ? <Countries Data={this.state.data}/> : null }
              </div>
              <div className="col-2">
                <button
                  type="submit" 
                  value="Refresh"
                  onClick={refreshData}
                >
                  Refresh
                </button>
              </div>
            </div>
          </div>
        </header>
      </div>
    )}
}

export default Users;
