        <div>
            Ãœlkeler
        </div>
    );
}



export class Users extends Component {
  state = {
    showResult: false,
    data: {}    
  }

  componentDidMount() {
    
    this.refreshData();
  }
  
  getData() {
    fetch('http://localhost:8888/graphql', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: `
                {users{
                    name
                    users{
                        name {
                        title
                        first
                        last
                        }
                        gender
                        email
                    }
                }}
            `,
          variables: {
            now: new Date().toISOString(),
          },
        }),
      })
        .then((res) => res.json())
        .then((result) => {
            console.log(result);
            this.setState({data : result.data}) 
      });
  }

  refreshData() {
    this.getData();
    this.setState({showResult : true})
    alert(this.state.showResult);
  }

  render() {
    return (
      <div>
        <header id="users">
          <div className="container">
            <div className="row">
              <div className="col-10">
                { this.state.showResult ? <Countries/> : null }
              </div>
              <div className="col-2">
                <button
                  type="submit" 
                  value="Refresh"
                  onClick={this.refreshData}
                >
                  Refresh
                </button>
              </div>
            </div>
          </div>
        </header>
      </div>
    )}
}

export default Users;
