import React, { Component } from 'react'
import ReactDOM from 'react-dom/client';
import './User.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown'
//import $ from 'jquery';


var Helper = {
  GetData: () => {
    fetch('http://localhost:8888/graphql', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        query: `
              {users{
                  name
                  users{
                      name {
                      title
                      first
                      last
                      }
                      gender
                      email
                  }
              }}
          `,
        variables: {
          now: new Date().toISOString(),
        },
      }),
    })
      .then((res) => res.json())
      .then((result) => {
          console.log(result);
          this.Data=result.data; 
          return true;
          //this.setState({data : result.data}) 
      });
      return false;
  },
  Data : {}
}


var Refresh = React.createClass({
  mixins: [Helper],
  getInitialState: () => {
      return { showResults: false };
  },
  onClick: () => {
    this.setState({ showResults: this.GetData() });
  },
  render: () => {
      return (
          <div>
              <input type="submit" value="Search" onClick={this.onClick} />
              { this.state.showResults ? <Countries /> : null }
          </div>
      );
  }
});

var Countries = React.createClass({
  mixins: [Helper],
  getInitialState: ()=> {
      return { 
        showResults: false,
        data: {},
        country: "", 
      };
  },
  onSelect: (e)=> {
    this.setState({ country : e });
    this.setState({ showResults: 
      this.Data !== null && 
      this.Data !== undefined ? true : false
    });
  },
  componentDidMount: ()=>{
    if (
      this.Data !== null && 
      this.Data !== undefined 
    )
    {
      this.setState({data : this.Data});
    } 
  },
  render: function() {
      return (
          
          <div>
              <DropdownButton
                  title="Please select a Country"
                  id="countries"
                  onSelect={this.onSelect}
                  >
                    {this.state.data.users
                    ? this.state.data.users.map((d, i) => (
                        <Dropdown.Item eventKey={'divCountry'+i}>{d.name}</Dropdown.Item>
                    ))
                    : "Countries not loaded"}
                </DropdownButton>
              { this.state.showResults ? <Country name={this.state.country}/> : null }
          </div>
      );
  }
});

