import React, { Component } from 'react'
//import JsonData from './data/data.json';
import './App.css';

export class Users extends Component {
  state = {
    data: {}
  }

  getSunbau() {
    fetch('http://localhost:8888/graphq', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: `
                {users{
                    name
                    users{
                        name {
                        title
                        first
                        last
                        }
                        gender
                        email
                    }
                }}
            `,
          variables: {
            now: new Date().toISOString(),
          },
        }),
      })
        .then((res) => res.json())
        .then((result) => {
            console.log(result);
            this.setState({data : result}) 
        });

  }

  componentDidMount() {
    this.getSunbau();
  }

  render() {
    return (
      <div>
          <header id="users">
            <div className="container">
                <div className="row">
                <div id="subHeader">{" "}</div>
                {this.state.data.Users
                    ? this.state.data.Users.map((d, i) => (
                        <div  key={`divhome-${i}`} className="col-md-4 col-sm-8 thema">
                        <div className="thumbnail">
                            {" "}
                            
                            <div className="caption">
                                <h4>{d.name}</h4>
                                <p>{d.Users}</p>
                            </div>
                        </div>
                        </div>
                    ))
                    : "loading"}
                </div>
            </div>
          </header>
      </div>
    );
  }
}

export default Users;
