import {React,Component} from 'react'
import './User.css';
import 'bootstrap/dist/css/bootstrap.min.css';
//import $ from 'jquery';

export class Countries extends Component {
  render() {
  return (
      <div>
        <div className="container">
          <h4>{this.props.name}</h4>
          <div className="row">
              <div className="col-2">Title</div>
              <div className="col-2">First Name</div>
              <div className="col-3">Last Name</div>
              <div className="col-1">Gender</div>
              <div className="col-4">Email</div>
          </div>
          {this.state.data.users.map((user,j) => {
            return (
                <div className="row" key={'divUser'+j}>
                    <div className="col-2">{user.name.title}</div>
                    <div className="col-2">{user.name.first}</div>
                    <div className="col-3">{user.name.last}</div>
                    <div className="col-1">{user.gender}</div>
                    <div className="col-4">{user.email}</div>
                </div>
              )
          })}
        </div>
      </div>
    );
  }
}


export class Users extends Component {
  state = {
    refreshData: false,
    data: {},
    showList: false,    
  }

  componentDidMount() {
    //console.log("geldi")
    this.getData();
    
  }

  // componentWillUnmount() {
  //   alert("gelecek")
  // }

  componentDidUpdate() {
    if(this.state.refreshData)
    { 
      //console.log("test")
      this.getData();
    }
    //console.log(this.state.data)
  }
  
  getData() {
    fetch('http://localhost:8888/graphql', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: `
                {users{
                    name
                    users{
                        name {
                        title
                        first
                        last
                        }
                        gender
                        email
                    }
                }}
            `,
          variables: {
            now: new Date().toISOString(),
          },
        }),
      })
        .then((res) => res.json())
        .then((result) => {
            //console.log(result);
            this.setState({
              data : result.data,
              refreshData:false,
              showList:true
            }) 
      });
  }

  render() {

    const refreshData=(e) => {
      this.setState({refreshData: true})
    }

    return (
      <div>
        <header id="users">
          <div className="container">
            <div className="row">
              <div className="col-10">
                { this.state.showList ? <Countries data={this.state.data}/> : null }
              </div>
              <div className="col-2">
                <button
                  type="submit" 
                  value="Refresh"
                  onClick={refreshData}
                >
                  Refresh
                </button>
              </div>
            </div>
          </div>
        </header>
      </div>
    )}
}

export default Users;
