import {React,Component} from 'react'
import './User.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown'
//import $ from 'jquery';

export class Country extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      country: props.name,
      data: props.data,
    };

  }
  render() {
    return (<div>Ãœlkedsadas</div>)
  }
}

export class Countries extends Component {
  
  constructor(props) {
    super(props);
    this.state = { 
      country: "",
      data: {},
      showList: false,     
    };

    this.onSelect = this.onSelect.bind(this);
  }

  onSelect (e){
    this.setState({ 
      country : e,
      showList: true
    });
  }
  
  render() {
    
    let countryData = () => {
      const data = this.props.data.Users.filter(u => u.id === this.state.country);
      this.setState({ data: data });
    }

    return (
          <div>
            <header id="countries">
            <div className="container">
              <div className="row">
                  <div className="col-12">
                    <DropdownButton
                      title="Please select a Country"
                      id="countries"
                      onSelect={this.onSelect}
                      >
                        {this.props.data.users
                        ? this.props.data.users.map((d, i) => (
                            <Dropdown.Item key={i} eventKey={d.name}>{d.name}</Dropdown.Item>
                        ))
                        : "Countries not loaded"}
                      </DropdownButton>

                  </div>
                  { 
                    this.state.showList ?
                    <div className="col-12">
                        <Country name={this.state.country} data={this.state.data}/>
                    </div>:
                    null
                  }
                </div>
              </div>
            </header>
          </div>
    );
  }
}


export class Users extends Component {
  state = {
    refreshData: false,
    data: {},
    showList: false,    
  }

  componentDidMount() {
    this.getData();
  }

  componentDidUpdate() {
    if(this.state.refreshData)
    { 
      this.getData();
    }
  }
  
  getData() {
    fetch('http://localhost:8888/graphql', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: `
                {users{
                    name
                    users{
                        name {
                        title
                        first
                        last
                        }
                        gender
                        email
                    }
                }}
            `,
          variables: {
            now: new Date().toISOString(),
          },
        }),
      })
        .then((res) => res.json())
        .then((result) => {
            this.setState({
              data : result.data,
              refreshData:false,
              showList:true
            }) 
      });
  }

  render() {

    const refreshData=(e) => {
      this.setState({refreshData: true})
    }

    return (
      <div>
        <br/>{"  "}
        <header id="users">
          <div className="container">
            <div className="row">
              <div className="col-10">
                { this.state.showList ? <Countries data={this.state.data}/> : null }
              </div>
              <div className="col-2">
                <button
                  className="btn btn-primary"
                  type="submit" 
                  value="Refresh"
                  onClick={refreshData}
                >
                  Refresh
                </button>
              </div>
            </div>
          </div>
        </header>
      </div>
    )}
}

export default Users;
