import React, { Component } from 'react'
//import JsonData from './data/data.json';
import './App.css';

export class Users extends Component {
  state = {
    data: {}
  }

  getSunbau() {
    fetch('http://localhost:8888/graphql', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: `
                {users{
                    name
                    users{
                        name {
                        title
                        first
                        last
                        }
                        gender
                        email
                    }
                }}
            `,
          variables: {
            now: new Date().toISOString(),
          },
        }),
      })
        .then((res) => res.json())
        .then((result) => {
            console.log(result);
            this.setState({data : result.data}) 
        });

  }

  componentDidMount() {
    this.getSunbau();
  }

  render() {
    return (
      <div>
          <header id="users">
            <div className="container">
                <div className="row">
                <div id="subHeader">{" "}</div>
                {this.state.data.users
                    ? this.state.data.users.map((d, i) => (
                        <div  key={`divhome-${i}`} className="col-md-4 col-sm-8 thema">
                            <div className="thumbnail">
                                {" "}
                                <div className="caption">
                                    <h4>{d.name}</h4>
                                    <table>
                                        <tr>
                                            <th>Title</th>
                                            <th>First Name</th>
                                            <th>Last Name</th>
                                            <th>Gender</th>
                                            <th>Email</th>
                                        </tr>
                                    {d.users.map((user,j) => {
                                        return (
                                            <div  key={`divhome-${j}`}>
                                                <><td>{user.name.title}</td><td>{user.name.first}</td><td>{user.name.last}</td><td>{user.gender}</td><td>{user.email}</td></>
                                            </div> 
                                        )
                                    })}
                                    </table>

                                </div>
                            </div>
                        </div>
                    ))
                    : "loading"}
                </div>
            </div>
          </header>
      </div>
    );
  }
}

export default Users;
