import React, { Component } from 'react'
import './User.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown'
import $ from 'jquery';

var Search= React.createClass({
  handleClick: function (event) {
      console.log(this.prop);
  },
  render: function () {
      return (
          <div className="date-range">
              <input type="submit" value="Search" onClick={this.handleClick} />
          </div>
      );
  }
});

export class Users extends Component {
  state = {
    data: {}
  }

  getSunbau() {
    fetch('http://localhost:8888/graphql', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: `
                {users{
                    name
                    users{
                        name {
                        title
                        first
                        last
                        }
                        gender
                        email
                    }
                }}
            `,
          variables: {
            now: new Date().toISOString(),
          },
        }),
      })
        .then((res) => res.json())
        .then((result) => {
            console.log(result);
            this.setState({data : result.data}) 
        });

  }

  componentDidMount() {
    this.getSunbau();
  }
  

  
  render() {
    function handleSelect(e) {
      //$(".countries").removeClass("d-inline");
      var item = "#"+e
      alert(item);
      $(item).addClass("d-inline");
    }
    return (
      <div>
          <header id="users">
            
            <div className="container">
                <br/>{" "}
                <DropdownButton
                  title="Please select a Country"
                  id="countries"
                  onSelect={handleSelect}
                  >
                    {this.state.data.users
                    ? this.state.data.users.map((d, i) => (
                        <Dropdown.Item eventKey={'divCountry'+i}>{d.name}</Dropdown.Item>
                    ))
                    : "loading"}
                </DropdownButton>
                {this.state.data.users
                    ? this.state.data.users.map((d, i) => (
                        <div className="countries row" key={'divCountry'+i}>
                            <div className="container">
                                <h4>{d.name}</h4>
                                <div className="row">
                                    <div className="col-2">Title</div>
                                    <div className="col-2">First Name</div>
                                    <div className="col-3">Last Name</div>
                                    <div className="col-1">Gender</div>
                                    <div className="col-4">Email</div>
                                </div>
                            
                                {d.users.map((user,j) => {
                                    return (
                                        <div className="row" key={'divUser'+i+j}>
                                            <div className="col-2">{user.name.title}</div>
                                            <div className="col-2">{user.name.first}</div>
                                            <div className="col-3">{user.name.last}</div>
                                            <div className="col-1">{user.gender}</div>
                                            <div className="col-4">{user.email}</div>
                                        </div>
                                    )
                                })}
                            </div>
                        </div>
                    ))
                    : "loading"}
                    
                </div>
          </header>
      </div>
    );
  }
}

export default Users;
