import React, { Component } from 'react'
import ReactDOM from 'react-dom/client';
import './User.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown'
//import $ from 'jquery';


var Helper = {
  GetData: () => {
    fetch('http://localhost:8888/graphql', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        query: `
              {users{
                  name
                  users{
                      name {
                      title
                      first
                      last
                      }
                      gender
                      email
                  }
              }}
          `,
        variables: {
          now: new Date().toISOString(),
        },
      }),
    })
      .then((res) => res.json())
      .then((result) => {
          console.log(result);
          this.Data=result.data; 
          return true;
          //this.setState({data : result.data}) 
      });
      return false;
  },
  Data : {}
}




var Country = React.createClass({
  mixins: [Helper],
  getInitialState: ()=> {
      return { 
        data: {} 
      };
  },
  componentDidMount: ()=>{
    if (
      this.Data !== null && 
      this.Data !== undefined 
    )
    {
      var usersOfCOuntry = this.Data.users.filter(x => function (x){
        if(x.name === this.props.name)
        {
          return x.users;
        }
      })
      console.log(usersOfCOuntry)
      this.setState({data : usersOfCOuntry});
    } 
  },
  render: function() {
      return (
          <div>
            <div className="container">
              <h4>{this.props.name}</h4>
              <div className="row">
                  <div className="col-2">Title</div>
                  <div className="col-2">First Name</div>
                  <div className="col-3">Last Name</div>
                  <div className="col-1">Gender</div>
                  <div className="col-4">Email</div>
              </div>
              {this.state.data.users.map((user,j) => {
                  return (
                      <div className="row" key={'divUser'+j}>
                          <div className="col-2">{user.name.title}</div>
                          <div className="col-2">{user.name.first}</div>
                          <div className="col-3">{user.name.last}</div>
                          <div className="col-1">{user.gender}</div>
                          <div className="col-4">{user.email}</div>
                      </div>
                  )
              })}
          </div>
        </div>
      );
  }
});

var Refresh = React.createClass({
  mixins: [Helper],
  getInitialState: () => {
      return { showResults: false };
  },
  onClick: () => {
    this.setState({ showResults: this.GetData() });
  },
  render: () => {
      return (
          <div>
              <input type="submit" value="Search" onClick={this.onClick} />
              { this.state.showResults ? <div>deneme</div> : null }
          </div>
      );
  }
});


export class Users extends Component {
  render() {
    return (
      <div>
          <header id="users">
              <div className="container">
                   <Refresh></Refresh>
              </div>
          </header>
      </div>
    );
  }
}

export default Users;
